mapper.delete:
DELETE tbl_common, tbl_likes, tbl_comment, tbl_img
FROM tbl_common
         LEFT JOIN tbl_likes ON tbl_common.id = tbl_likes.article_id
         LEFT JOIN tbl_comment ON tbl_common.id = tbl_comment.article_id AND type = #{articleType}
         LEFT JOIN tbl_img ON tbl_common.id = tbl_img.article_id AND article_type = #{articleType}
WHERE tbl_common.id = #{id, jdbcType=INTEGER};
